function social_load_textdomain(){
	load_plugin_textdomain('popup',false,dirname(__FILE__).'/languages');
}
add_action('plugin_loaded','social_load_textdomain');
function social_share_button($content){
	$current_post_link= get_the_permalink();
	$title = get_the_title();
	if (is_single()){
	$social_list = <<<SOCIAL
	<div class="social-share-button" style="margin:10px 0px; text-align:right">
          <a  href="https://www.facebook.com/sharer/sharer.php?u={$current_post_link}" target="_blank"> <img width="130px" alt="Facebook Share" src="data:image/png;base64," /></a>
          <a  href="http://pinterest.com/pin/create/button/?url={$current_post_link}" target="_blank"><img width="130px" alt="Pinterest Share" src="data:image/png;base64," /></a>
          <a  href="https://www.linkedin.com/sharing/share-offsite/?url={$current_post_link}" target="_blank"><img width="130px" alt="Linkedin Share" src="data:image/png;base64," /></a>
          <a  href="http://twitter.com/share?text={$title}&url={$current_post_link}" target="_blank"><img width="130px" alt="Twitter Share" src="data:image/png;base64," /></a>
	</div>
SOCIAL;
	$content .= $social_list;
	echo $content;
	}
}

add_action('the_content','social_share_button');
